import React,{useEffect} from 'react'
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {useCart,useDispatchCart} from '../components/ContextReducer'


 const Cart= ()=> {
  const Data = useCart();
  const dispatch = useDispatchCart();

  if (Data.length === 0) {
    return (
      <div>
        <div className='m-5 w-100 text-center fs-3'>The Cart is Empty!</div>
      </div>
    )
  }
  const handleRemove = (index)=>{
    dispatch({type:"REMOVE",index:index})
  }

  const loginEmail = localStorage.getItem('userEmail')
  console.log(Data.ema);



  const handleCheckOut = async()=>{

    // fetched from login page//


     await fetch('http://localhost:5000/orderdata',{ 
      method :'POST',
      headers: {
              'Content-Type': 'application/json'
            },

      body: JSON.stringify({ email:loginEmail, fdata:Data , date: new Date().toDateString()})

    }).then(()=>{
        dispatch({type:'DROP'})

    }).catch((err)=>{
      console.log('error :',err);
    })

  }

  // useEffect(()=>{
  //   handleCheckOut();
  // },[])

   let totalPrice = Data.reduce((total, item) => total + item.price, 0)
  return (
    <div>
      <div className='container m-auto mt-5 table-responsive  table-responsive-sm table-responsive-md' >
        <table className='table table-hover '>
          <thead className=' text-success fs-4'>
            <tr>
              <th scope='col' >#</th>
              <th scope='col' >Name</th>
              <th scope='col' >Quantity</th>
              <th scope='col' >Option</th>
              <th scope='col' >Amount</th>
              <th scope='col' >Delete</th>
            </tr>
          </thead>
          <tbody>


            { 
              Data.map((item, index) => (
              <tr>
                <th scope='row' >{index + 1}</th>
                <td >{item.name}</td>
                <td>{item.qty}</td>
                <td>{item.size}</td>
                <td>{item.price}</td>
                <td ><button type="button" className="btn p-0" onClick={handleRemove}>delete</button> </td>

                </tr>
            ))}
          </tbody>
        </table> 
        <div><h1 className='fs-2'>Total Price: {totalPrice}/-</h1></div>
        <div>
          <button className='btn bg-success mt-5 ' onClick={handleCheckOut} > Check Out </button>
        </div>
      </div>
    </div>
  )
}

export default Cart
