import React,{useEffect, useState} from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'



const AllOrder =  () => {

    const [orderData, setorderData] = useState([])
   const dataEmail = localStorage.getItem('userEmail')


   const loadData = async()=>{
      const res = await fetch(`http://localhost:5000/myorderdata?email=${dataEmail}`, {
      method: 'GET',
      headers: {
          'content-Type': 'application/json'
      },
    //   body: JSON.stringify({email : dataEmail})
   })

      let response = await res.json()
       setorderData(response.order_data)
      console.log(response.order_data);

  }

  useEffect(() => {
      loadData();
  },[])

  return (
      <div>
          <div>
              <Navbar />
          </div>

              <div className='container'>
                  {/* <div className='row'> */}
                  {orderData.map((item)=>{
                    <>
                    {console.log(item.img)}
                    <div className="card" style="width: 18rem;">
  <img src="https://images.unsplash.com/photo-1582454235987-1e597bafcf58?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8OXx8c3ByaW5nJTIwcm9sbHN8ZW58MHx8MHx8fDA%3D&auto=format&fit=crop&w=500&q=60" className="card-img-top" alt="..."   style={{height:"200px", objectFit:"cover",padding:'5px'}}/>
  <div className="card-body">
    <h5 className="card-title">{item.name}</h5>
    <a href="#" className="btn btn-primary">Go somewhere</a>
  </div>
</div>
</>

                  })}

                  {/* </div> */}
              </div> 


          <Footer />
      </div>
  )
}

export default AllOrder;

































































