import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

import {useCart,useDispatchCart} from '../components/ContextReducer'


 const Cart= ()=> {
  const Data = useCart();
  const dispatch = useDispatchCart();
  if (Data.length === 0) {
    return (
      <div>
        <div className='m-5 w-100 text-center fs-3'>The Cart is Empty!</div>
      </div>
    )
  }
  const handleRemove = (index)=>{
    console.log(index)
    dispatch({type:"REMOVE",index:index})
  }

  const handleCheckOut = async ()=>{
    let loginEmail = localStorage.getItem('userEmail') // fetched from login page//
    let response = await fetch('http://localhost:5000/users/orders',{
      Method: 'POST',
      headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ fdata:Data, email:loginEmail, date: new Date.toDaString()})
    })
      if(response === 200){
        dispatch({type:'DROP'})
      }
  }

  // const handleCheckOut = async () => {
  //   let userEmail = localStorage.getItem("userEmail");
  //   // console.log(data,localStorage.getItem("userEmail"),new Date())
  //   let response = await fetch("http://localhost:5000/api/auth/orderData", {
  //     // credentials: 'include',
  //     // Origin:"http://localhost:3000/login",
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json'
  //     },
  //     body: JSON.stringify({
  //       order_data: data,
  //       email: userEmail,
  //       order_date: new Date().toDateString()
  //     })
  //   });
  //   console.log("JSON RESPONSE:::::", response.status)
  //   if (response.status === 200) {
  //     dispatch({ type: "DROP" })
  //   }
  // }

   let totalPrice = Data.reduce((total, item) => total + item.price, 0)
  return (
    <div>
      <div className='container m-auto mt-5 table-responsive  table-responsive-sm table-responsive-md' >
        <table className='table table-hover '>
          <thead className=' text-success fs-4'>
            <tr>
              <th scope='col' >#</th>
              <th scope='col' >Name</th>
              <th scope='col' >Quantity</th>
              <th scope='col' >Option</th>
              <th scope='col' >Amount</th>
              <th scope='col' >Delete</th>
            </tr>
          </thead>
          <tbody>


            { 
              Data.map((item, index) => (
              <tr>
                <th scope='row' >{index + 1}</th>
                <td >{item.name}</td>
                <td>{item.qty}</td>
                <td>{item.size}</td>
                <td>{item.price}</td>
                <td ><button type="button" className="btn p-0" onClick={handleRemove}>delete</button> </td>

                </tr>
            ))}
          </tbody>
        </table> 
        <div><h1 className='fs-2'>Total Price: {totalPrice}/-</h1></div>
        <div>
          <button className='btn bg-success mt-5 ' onClick={handleCheckOut} > Check Out </button>
        </div>
      </div>
    </div>
  )
}

export default Cart
