import React, { useState, useEffect } from 'react'
import Navbar from '../components/Navbar'
import Carousel  from '../components/Carousel'
import Footer from '../components/Footer'
import Card from '../components/Card'

const Home = () => {
    const [foodcat, setfoodcat] = useState([])
    const [fooditem, setfooditem] = useState([])


    const loadData = async () => {
        let response = await fetch('http://localhost:5000/cart', {
            method: 'GET',
            headers: {
                'content-Type': 'application/json'
            }
        })
        response = await response.json();
        setfoodcat(response[1]);
        setfooditem(response[0]);

    }

    useEffect(() => {
        loadData();
    })


    return (
        <div>
            <div><Navbar /></div>
            <div><Carousel/></div>
            {/* search  */}
            <div className='' id='sea' style={{ zIndex: "10" }}>
                <form className="d-flex " role="search">
                    <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-warning text-white" type="submit">Search</button>
                 </form>
            </div>
            <div className='container'>
                {
                    foodcat !== [] ? foodcat.map((data) => {
                        return (<div className='row '>
                            <div key={data._id} className='fs-3 m-3'> {data.catname} </div>
                            <hr />
                            {fooditem !== [] ? fooditem.filter((item) => item.catname === data.catname).map((filteritem) => {
                                return (<div key={filteritem._id} className='col-12 col-md-6 col-lg-3'>
                                    <Card
                                        foodname={filteritem.fdname}
                                        img={filteritem.fdimg}
                                        option={filteritem.options[0]}
                                        des={filteritem.description}
                                    />


                                </div>)
                            }) : ""}
                        </div>
                        )
                    }) : <div> nothing  happen</div>
                }
            </div>
            <div><Footer /></div>
        </div>

    )
}


export default Home;
