import React, { createContext, useContext, useReducer } from "react";


const startContext = createContext();
const dispatchContext = createContext();

// action perform  here//
const reducer = (state, action) => {
    switch(action.type){
        case 'ADD':
            return [...state,{id: action._id,name:action.fdname, img:action.img, qty:action.qty, size:action.size, price: action.price}]
        default:
            console.log('error in reducer');    
    }
}

export const CartProvider = ({ child }) => {
    // the action on the state is appended to dispatch / /
    const [state, dispatch] = useReducer(reducer, [])
    return (
        <dispatchContext.Provider value={dispatch}>
            <startContext.Provider value={state}>
                {child}
            </startContext.Provider>
        </dispatchContext.Provider>
    )
}

export const useCart =()=> useContext(startContext);
export const useDispatchcart= ()=>useContext(dispatchContext);
